import matplotlib.pyplot as plt

movies = ["Extraction", "Dasara", "Ae Dil Hai Mushkil", "Clifford the Big Red Dog","Thunivu"]
scores = [
    [65, 80, 85, 90, 95],
    [70, 55, 80, 95, 100],
    [60, 85, 80, 95, 100],
    [70, 55, 80, 85, 90],
    [75, 80, 85, 90, 95]
]

plt.boxplot(scores, labels=movies)
plt.title("WeSpoil accuracy in detecting spoilers and assigning spoiler scale ratings")
plt.ylabel("Score")
plt.xlabel("Movie")
plt.show()
